package example;

public class example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b;
		double x,y,z;
		System.out.println("1: "+(x=20));
		y=10.56789;
		a=(int)y;
		System.out.println("2: "+a);
		z=a;
		System.out.println("3: "+z);
		x=5;
		y=14;
		a=5;
		b=14;
		System.out.println("4: "+(y/x));
		System.out.println("5: "+(b/a));
		System.out.println("6: "+(b%a));
		System.out.println("7: "+(a%a));
		System.out.println("8: "+(y/a));
		System.out.println("9: "+((double)b/a));
		System.out.println("10: "+((double)(b/a)));
		System.out.println("11: "+((int)(y/x)));
		y=25.8;
		System.out.println("12: "+((int)y/x));
		y=36.6;
		x=6.9;
		System.out.println("13: "+((int)y/(int)x));


	}

}
